x;\">\n                <button type=\"submit\" class=\"enterbutton submitButton pure-u-1 pure-button pure-button-primary\"\n                        disabled=\"true\">Вход</button>\n            </div>\n        </form>\n    </div>\n</noindex>\n</div>\n<script type=\"text/javascript\">\n    var authToken = '7eae552f446ce88bbb15da45d0d8445b1d8ffd5a';\n    var economMode = 'false' == 'true';\n    var locale = 'ru';\n    var userId = '';\n    var autoPlay = 'false' == 'true';\n</script>\n<!-- press-js: jquery.simplemodal.js -->\n<!-- press-js: jqtmpl.js -->\n<!-- press-js: i18n.js -->\n<!-- press-js: hotKeys.js -->\n<!-- press-js: textchange.js -->\n<!-- press-js: hashchange.js -->\n<!-- press-js: util.js -->\n<!-- press-js: comments.js -->\n<!-- press-js: tabs.js -->\n<!-- press-js: browser-update.js -->\n<script src=\"/press/js/WfGCzZRDNEKkCCASlHpFGgJJ.js\" type=\"text/javascript\" language=\"javascript\" charset=\"utf-8\"></script>\n<script type=\"text/javascript\">\n    $(\"#loginbutton\").show();\n</script>\n<script id=\"commentTmpl\" type=\"text/x-jquery-tmpl\">\n    <div class=\"comment comment{{= id}}\"\n    {{if offset > 15}}style=\"padding-left: 0px; border: none;\"{{/if}}>\n    <div class=\"content\">\n        <div class=\"head\">@rating: <span class=\"rating bolder\">{{= voteCount}}</span>\n            <span class=\"author\">@author: <a href=\"/profile/{{= authorName}}\">{{= authorName}}</a></span>\n            <span class=\"date\">@date: {{= date}}</span>\n            <span class=\"link\">@link: <a href=\"/{{= entryId}}#comment{{= id}}\"\n                                         onclick=\"goToComment({{= id}});\"\n                                         title=\"Ссылка на комментарий\">#</a></span>\n        </div>\n        {{if deleted}}\n        <div class=\"foot deleted\"><span class=\"folding\" onclick=\"javascript:fold({{= id}});\"\n                                        title=\"Свернуть ветку\">-</span> Комментарий удален{{if\n            deleteReason}}